mapper.update:
update `position`
name = #{name}, content = #{content}, employ_id = #{employId}, type = #{type}, experience = #{experience}, salary = #{salary}, education = #{education}, content = #{content}, tag = #{tag}, status = #{status}, industry_id = #{industryId},
id = #{id}

mapper.select:
select position.*, employ.name as employName, employ.avatar as employAvatar,employ.stage as employStage, industry.name as industryName from `position`
left join `system`.employ on position.employ_id = employ.id
left join `system`.industry on position.industry_id = industry.id

    and position.industry_id = #{industryId}
    and position.employ_id = #{employId}
    and position.status = #{status}
