mapper.select:
select submit.*, e.name as employName, p.name as positionName, u.name as userName from `submit`
left join `system`.employ e on e.id = submit.employ_id
left join `system`.position p on p.id = submit.position_id
left join `system`.user u on u.id = submit.user_id

    and submit.userId = #{userId}
    and submit.positionId = #{positionId}
    and submit.employ_id = #{employId}

mapper.update:
update `submit`

    status = #{status},


    and user_id = #{userId}
    and position_id = #{positionId}
    and employ_id = #{employId}
    and resume_id = #{resumeId}
